mapper.update:
update teaching_resource
set title = #{title}
where id = #{id} and course_id = #{courseId} and is_deleted = 0

mapper.select:
select id, title, description, resource_type, parent_id, file_urls, file_names, file_sizes,course_id,uploader_name, download_count from teaching_resource

    id = #{id}
    and title like concat('%',#{title},'%')
    and course_id = #{courseId}
    and parent_id = #{parentId}
    and parent_id is null
    and is_deleted = 0

order by create_time desc
